#!/usr/bin/env bash
########################################
# Jody Lent
# Script for building a new Mac

# Settings stolen from:
# https://github.com/mathiasbynens/dotfiles/blob/master/.macos
# https://github.com/lra/mackup/tree/master/doc
########################################


####################
# Karabiner
####################

# Copy Karabiner settings
# I only have one setting. For large files use:
#   export: `/Applications/Karabiner.app/Contents/Library/bin/karabiner export > karabiner-import.sh`
#   import: `bash ${STRAPDIR}/karabiner-import.sh`
/Applications/Karabiner.app/Contents/Library/bin/karabiner set remap.pc_application2optionR 1


####################
# Karabiner-Elements
####################

# Karabiner only works on mac OS 10.9-11, not on Sierra yet, so we use the beta/non-pretty dev product
# https://pqrs.org/osx/karabiner/
mkdir -p ~/.karabiner.d/configuration
cat > ~/.karabiner.d/configuration/karabiner.json << EOF
{
    "profiles": [
        {
            "name": "MS_ComfortCurve4000",
            "selected": true,
            "simple_modifications": {
                "caps_lock": "left_control",
                "left_option": "left_command",
                "left_command": "left_option",
                "right_option": "right_command",
                "application" : "right_option"
            }
        }
    ]
}
EOF
wget -O /tmp/karabiner-elements-latest.dmg https://pqrs.org/latest/karabiner-elements-latest.dmg
echo "PLEASE RUN 'open /tmp/Karabiner-Elements*dmg'"
# Installs to:
# /Applications/Karabiner-Elements.app/Contents/MacOS/Karabiner-Elements

####################
# Spectacle
####################

# defaults write com.divisiblebyzero.Spectacle THING -data bighexstring
# pull your existing with the following:
# defaults read com.divisiblebyzero.Spectacle|
# sed -e "s/ \= \</\@\@\@\-data\@\@\@/g"|
# sed -e "s/^    /\@\@\@/g"|
# sed -e "s/[ |\>\;|^{]//g"|
# sed -e "s/\@\@\@/ /g"|
# sed -e "s/^ /defaults write com.divisiblebyzero.Spectacle /g"|pbcopy
# YOU MAY NOT WANT IT ALL

defaults write com.divisiblebyzero.Spectacle MakeLarger -data 
defaults write com.divisiblebyzero.Spectacle MakeSmaller -data 
defaults write com.divisiblebyzero.Spectacle MoveToBottomHalf -data 
defaults write com.divisiblebyzero.Spectacle MoveToCenter -data 
defaults write com.divisiblebyzero.Spectacle MoveToFullscreen -data 
defaults write com.divisiblebyzero.Spectacle MoveToLeftHalf -data 
defaults write com.divisiblebyzero.Spectacle MoveToLowerLeft -data 
defaults write com.divisiblebyzero.Spectacle MoveToLowerRight -data 
defaults write com.divisiblebyzero.Spectacle MoveToNextDisplay -data 
defaults write com.divisiblebyzero.Spectacle MoveToNextThird -data 
defaults write com.divisiblebyzero.Spectacle MoveToPreviousDisplay -data 
defaults write com.divisiblebyzero.Spectacle MoveToPreviousThird -data 
defaults write com.divisiblebyzero.Spectacle MoveToRightHalf -data 
defaults write com.divisiblebyzero.Spectacle MoveToTopHalf -data 
defaults write com.divisiblebyzero.Spectacle MoveToUpperLeft -data 
defaults write com.divisiblebyzero.Spectacle MoveToUpperRight -data 
defaults write com.divisiblebyzero.Spectacle RedoLastMove -data 
defaults write com.divisiblebyzero.Spectacle SUEnableAutomaticChecks=1
defaults write com.divisiblebyzero.Spectacle UndoLastMove -data 


####################
# SublimeText
####################

# Install Sublime Text settings
if [ -z ${SUBLIME_SETTINGS_PATH+x} ]; then
    echo "SUBLIME_SETTINGS_PATH is unset"
    exit 0
else
    echo "SUBLIME_SETTINGS_PATH is set to '$SUBLIME_SETTINGS_PATH'"
    cp ${SUBLIME_SETTINGS_PATH} ~/Library/Application\ Support/Sublime\ Text*/Packages/User/Preferences.sublime-settings 2> /dev/null
fi
# Symlink `subl` into /usr/local/bin
ln -sf "/Applications/Sublime Text.app/Contents/SharedSupport/bin/subl" /usr/local/bin/subl
